import { Injectable } from '@angular/core';
import { Product } from '../models/product';
import { Category } from '../models/category';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  products: Product[]=[
    {id:1, name:"Book",price:22,categoryId:1,imgUrl:"data:image/jpeg;base64,"},
    {id:2, name:"mobile",price:22,categoryId:2,imgUrl:"data:image/jpeg;base64,"},
    {id:3, name:"phone",price:22,categoryId:2,imgUrl:"data:image/jpeg;base64,"},
    {id:4, name:"t-shirt",price:22,categoryId:1,imgUrl:"data:image/jpeg;base64,"},
    {id:5, name:"t-shirt",price:22,categoryId:3,imgUrl:"data:image/jpeg;base64,"},
    {id:6, name:"balto",price:22,categoryId:3,imgUrl:"data:image/jpeg;base64,"},
    {id:7, name:"t-shirt",price:22,categoryId:3,imgUrl:"data:image/jpeg;base64,"},
  ]
  cats : Category[]=[
      {id:1 , name:"Mobiles"},
      {id:2 , name:"Books"},
      {id:3 , name:"Cloths"},
    ]

  getAll(){return this.products}
  getAllCat(){return this.cats}

  getById(id: number){  // 3
    return this.products.find(p=>p.id === id)
  }

  getProductsByCategory(id : number){   //1
    return this.products.filter(p=>p.categoryId === id)
  }
  
  constructor() { }
}
